    public override void Update(GameTime gameTime)
    {

        if (Camera.atualiza)
        {
            x += Camera.Cam.X;
            rolagem += Camera.Cam.X;
        }

        if (rolagem <= -25)
        {
            if (Camera.fracao_mapa < (map[0].getMap().GetLength(1)))
            {
                Camera.fracao_mapa++;
                Camera.posizao++;


                if (margem > 0)
                    if (Camera.posizao > 1)
                    {
                        Camera.posizao++;
                        margem--;
                    }
            }

            rolagem = 0;
        }

        if (rolagem >= 25)
        {
            if (Camera.posizao > 0)
            {
                Camera.fracao_mapa--;
                Camera.posizao--;

                if (margem <= 5)
                {
                    Camera.posizao--;
                    margem++;
                }
            }
            rolagem = 0;
        }

        base.Update(gameTime);
    }

    public override void Draw(GameTime gameTime)
    {
        sprite.Begin();

        for (int y = 0; y < map[0].getMap().GetLength(0); y++)
        {
            for (int x = (int)Camera.posizao; x < Camera.fracao_mapa; x++)
            {
                sprite.Draw(tiles[map[0].getMap()[y, x]].textura,
                    new Vector2(this.x + (x * tiles[map[0].getMap()[y, x]].size.X), this.y + (y * tiles[map[0].getMap()[y, x]].size.Y)),
                    Color.White);
            }
        }
        sprite.End();
    }
